import re
def duplicate_whitespace_strip(string):
    #string = string.strip()

    # ensure space after prompt
    string = re.sub(r'[?]([^ ])', '? \\1', string)
    string = re.sub(r'[?]', '? ', string)

    # remove excessive spaces
    string = re.sub(r'[ ]{2,}', ' ', string)
    return string

